      left join kto_girokonto gir on zus.fk_main_key = gir.fk_main_key
      left join kto_kreditkarte kred on kred.fk_main_key = zus.fk_main_key

    where zus.fk_main_key = p_fk_main_key;  -- :PK_beleg;
    commit;
end;

