        merge into  t_rel_lex_kto_bel t1
        using (
                    select 
                        ll_rel.relation, kto.fk_main_key, ll_rel.jahr, kto.bucht_jahr, ll_rel.betrageur, kto."Betrag",
                        --substr(ll_rel.belegdat,1,10), substr(kto."Buchungstag",1,10), 
