'                end if;',
'                if m > l_max then exit; end if;',
'',
'            end loop;',
'',
'            l_offset := l_offset + l_chunksize;',
'            if m > l_max then exit; end if;',
'        end loop;',
'',
'        if l_leftover is not null then',
'            null;',
