'    end if;',
'',
'    sys.htp.prn(''<br />Components with 3 or more versions:''||to_char(l_apps_gt_2,''999G999G999G990''));',
'     if l_apps_gt_2 > 0 then',
'    sys.htp.prn('' [ <a href="''',
'        ||apex_util.prepare_url(''f?p=''||:APP_ID||'':13:''||:APP_SESSION||'':::13:P13_PURGE_ACTION:2V'')',
